mapper.update:
update users
set username=#{username},
    password=#{password},
    role_id=#{roleId}
where id = #{id}

update users
set password=#{newpassword}
where id = #{id}

mapper.select:
SELECT id, username, password, role_id
FROM users
WHERE username = #{username}

SELECT id, username, password, role_id
FROM users
WHERE id = #{id}
